<?php if ( $list->have_posts() ) {?><div id="<?php echo esc_attr( $widget_id ); ?>" class="widget bwp-product-<?php echo esc_attr($source); ?>">	<?php if( $title) : ?>	<div class="title-product-list"><h1 class="widget-title"><?php echo esc_html( $title ); ?></h1></div>	<?php endif; ?>	<div class="content-product-list">	<?php		while ( $list -> have_posts() ) : $list -> the_post();		global $product, $post, $wpdb;	?>		<div class="post-item clearfix">			<div class="item-img">				<a href="<?php the_permalink(); ?>" title="<?php the_title_attribute();?>">					<?php if( has_post_thumbnail() ){  						echo (get_the_post_thumbnail( $list->post->ID, 'shop_catalog' )) ? get_the_post_thumbnail( $list->post->ID, 'shop_catalog' ):'<img src="'.get_template_directory_uri().'/assets/img/placeholder/shop_thumbnail.png" alt="No thumb"/>' ;					}else{ 						echo '<img src="'.get_template_directory_uri().'/assets/img/placeholder/shop_thumbnail.png" alt="No thumb"/>' ;					} ?>				</a>			</div>			<div class="item-content">				<h4><a href="<?php the_permalink(); ?>" title="<?php the_title_attribute();?>"><?php the_title(); ?></a></h4>				<?php				$rating_count = $product->get_rating_count();				$review_count = $product->get_review_count();				$average      = $product->get_average_rating();				?>					<div class="woocommerce-product-rating" itemprop="aggregateRating" itemscope itemtype="http://schema.org/AggregateRating">						<div class="star-rating" title="<?php printf( __( 'Rated %s out of 5', 'wpbingo' ), $average ); ?>">							<span style="width:<?php echo ( ( $average / 5 ) * 100 ); ?>%">								<strong itemprop="ratingValue" class="rating"><?php echo esc_html( $average ); ?></strong> <?php printf( __( 'out of %s5%s', 'wpbingo' ), '<span itemprop="bestRating">', '</span>' ); ?>								<?php printf( _n( 'based on %s customer rating', 'based on %s customer ratings', $rating_count, 'wpbingo' ), '<span itemprop="ratingCount" class="rating">' . $rating_count . '</span>' ); ?>							</span>						</div>					</div>				<p><?php echo $product->get_price_html(); ?></p>			</div>		</div>	<?php 		endwhile;		wp_reset_postdata();	?>	</div></div><?php } ?>