<?php if ( $list->have_posts() ) {?><div id="<?php echo esc_attr( $widget_id ); ?>" class="bwp-product-<?php echo esc_attr($source); ?>">	<?php if( $title) : ?>	<div class="title-best-seller"><h2><?php echo esc_html( $title ); ?></h2></div>	<?php endif; ?>	<ul class="list-unstyled">	<?php		while ( $list -> have_posts() ) : $list -> the_post();		global $product, $post, $wpdb, $average;		$count = $wpdb->get_var($wpdb->prepare("			SELECT COUNT(meta_value) FROM $wpdb->commentmeta			LEFT JOIN $wpdb->comments ON $wpdb->commentmeta.comment_id = $wpdb->comments.comment_ID			WHERE meta_key = 'rating'			AND comment_post_ID = %d			AND comment_approved = '1'			AND meta_value > 0		",$post->ID));		$rating = $wpdb->get_var($wpdb->prepare("			SELECT SUM(meta_value) FROM $wpdb->commentmeta			LEFT JOIN $wpdb->comments ON $wpdb->commentmeta.comment_id = $wpdb->comments.comment_ID			WHERE meta_key = 'rating'			AND comment_post_ID = %d			AND comment_approved = '1'		",$post->ID));	?>		<li class="clearfix">			<div class="item-img">				<a href="<?php the_permalink(); ?>" title="<?php the_title_attribute();?>">					<?php if( has_post_thumbnail() ){  						echo (get_the_post_thumbnail( $list->post->ID, 'shop_catalog' )) ? get_the_post_thumbnail( $list->post->ID, 'shop_catalog' ):'<img src="'.get_template_directory_uri().'/assets/img/placeholder/shop_thumbnail.png" alt="No thumb"/>' ;					}else{ 						echo '<img src="'.get_template_directory_uri().'/assets/img/placeholder/shop_thumbnail.png" alt="No thumb"/>' ;					} ?>				</a>			</div>			<div class="item-content">				<h4><a href="<?php the_permalink(); ?>" title="<?php the_title_attribute();?>"><?php the_title(); ?></a></h4>                <p><?php echo $product->get_price_html(); ?></p>				<?php					if( $count > 0 ){						$average = number_format($rating / $count, 1);				?>					<div class="star"><span style="width: <?php echo ($average*14).'px'; ?>"></span></div>									<?php } else { ?>									<div class="star"></div>									<?php } ?>				<div class="review">					<span><?php echo $count; ?> <?php esc_html_e(' review(s) ', "wpbingo"); ?></span>				</div>			</div>		</li>	<?php 		endwhile;		wp_reset_postdata();	?>	</ul></div><?php } ?>